import { Injectable } from '@angular/core';
import { Food } from '../shared/models/Food';
import { sample_foods } from '../data';

@Injectable({
  providedIn: 'root'
})
export class FoodService {

  constructor() { }

  getAll():Food[]{
    return sample_foods;
  }

  getFoodFromSearch(food:string){
    this.getAll().filter(x=>x.name.toLowerCase().includes(food.toLowerCase()));
  }
}
