import { Component } from '@angular/core';
import { CartService } from 'src/app/services/cart.service';
import { UsersService } from 'src/app/services/users.service';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {

  cartCount:number = 0;
  userName:string= 'Login';
  constructor(private cartService:CartService, private user:UsersService){
    this.cartService.getCartObservable().subscribe((cart)=>{
      this.cartCount = cart.totalCount;
    });

    this.user.userObservable.subscribe((user)=>{
      this.userName = user.name;
    });
  }

  logOut(){
    this.user.logOutUser();
  }
}
<header>
    <div class="container">
        <a routerLink="/" class="logo">FoodBuddy</a>
        <nav>
            <ul>
                <li *ngIf="!userName">
                    <a routerLink="/login">Login</a>
                </li>
                <li class="menu-container">
                    <a routerLink="/dashboard">{{userName}}</a>
                    <div class="menu">
                        <a routerLink="/profile">Profiles</a>
                        <a routerLink="/orders">Orders</a>
                        <a routerLink="/logout">Logout</a>
                    </div>
                </li>
                <a routerLink="/cart-page">Cart <span>{{cartCount}}</span></a>
            </ul>
        </nav>
    </div>
</header>
